import React, { Component} from 'react'

class Counter extends Component {
    constructor(props) {
        super(props)
        this.state={
            count: props.initVal || 0
        }
    }

    render() {
        const { caption } = this.props
        return (
            <div>
                <button>+</button>
                <button>-</button>
                <span>{ caption }, count: {this.state.count}</span>
            </div>
        )
    }
}

export default Counter;
