import React, { Component, Fragment } from 'react';
import Counter from './Counter'

class CounterPanel extends Component {
    constructor(...args){
        super(...args)
        this.initState = [0, 10, 20]
        this.state = {
            sum: this.getSummary()
        }
    }
    render() {
        return (
            <Fragment>
                <Counter caption={'First'} initVal={this.initState[0]} />
                <Counter caption={'Second'} initVal={this.initState[1]}/>
                <Counter caption={'Third'} initVal={this.initState[2]}/>
                <span>Total count: { this.state.sum }</span>
            </Fragment>
        )
    }
    getSummary(){

        return this.initState.reduce((sum,n,i,arr) => {
            return sum += n
        },0)
    }
    countChange(newCount, preCount) {
        const val = newCount - preCount
        this.setState()
    }
}

export default CounterPanel;
